<?phpclass AutoLoadWorodYaar{	private static $_instance = null;	private function __construct()	{		spl_autoload_register([$this, 'load']);	}	public static function _instance()	{		if (!self::$_instance) {			self::$_instance = new AutoLoadWorodYaar();		}		return self::$_instance;	}	public function load($class)	{		if (is_readable(trailingslashit(WY_PLUGIN_DIR. 'class') . $class . '.php')) {			if (file_exists(trailingslashit(WY_PLUGIN_DIR . 'class') . $class . '.php')) {				include_once trailingslashit(WY_PLUGIN_DIR . 'class') . $class . '.php';			}		}		return;	}}AutoLoadWorodYaar::_instance();